import React, { useState } from 'react';
import axios from 'axios';

const AddMovieForm = ({ onAdd }) => {
  const [newMovie, setNewMovie] = useState({
    Movie_Name: '',
    Description: '',
    Rating: ''
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    
    if (!newMovie.Movie_Name || !newMovie.Description || isNaN(newMovie.Rating)) {
      alert('Please fill all the fields correctly.');
      return;
    }

    axios.post('http://localhost:5001/movies', newMovie)
      .then((response) => {
        onAdd(response.data); // Pass the newly added movie to parent component
        setNewMovie({ Movie_Name: '', Description: '', Rating: '' }); // Reset form
      })
      .catch((error) => {
        console.error('Error adding movie:', error);
      });
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        placeholder="Movie Name"
        value={newMovie.Movie_Name}
        onChange={(e) => setNewMovie({ ...newMovie, Movie_Name: e.target.value })}
      />
      <input
        type="text"
        placeholder="Description"
        value={newMovie.Description}
        onChange={(e) => setNewMovie({ ...newMovie, Description: e.target.value })}
      />
      <input
        type="number"
        placeholder="Rating"
        value={newMovie.Rating}
        onChange={(e) => setNewMovie({ ...newMovie, Rating: e.target.value })}
      />
      <button type="submit">Add Movie</button>
    </form>
  );
};

export default AddMovieForm;
