import React, { useState } from 'react';

const MovieList = ({ movies = [], editMovie, deleteMovie }) => {
  const [editingId, setEditingId] = useState(null);
  const [editedMovie, setEditedMovie] = useState({ Movie_Name: '', Description: '', Rating: '' });

  const handleEdit = (movie) => {
    setEditingId(movie.id);
    setEditedMovie(movie);
  };

  const handleSave = () => {
    if (editedMovie.Movie_Name && editedMovie.Description && !isNaN(editedMovie.Rating)) {
      editMovie(editingId, editedMovie);
      setEditingId(null);
    } else {
      alert("Please fill out all fields correctly.");
    }
  };

  return (
    <div>
      {movies.length > 0 ? (
        movies.map((movie) => (
          <div key={movie.id}>
            {editingId === movie.id ? (
              <>
                <input
                  type="text"
                  value={editedMovie.Movie_Name}
                  onChange={(e) => setEditedMovie({ ...editedMovie, Movie_Name: e.target.value })}
                />
                <input
                  type="text"
                  value={editedMovie.Description}
                  onChange={(e) => setEditedMovie({ ...editedMovie, Description: e.target.value })}
                />
                <input
                  type="number"
                  value={editedMovie.Rating}
                  onChange={(e) => setEditedMovie({ ...editedMovie, Rating: e.target.value })}
                />
                <button onClick={handleSave}>Save</button>
              </>
            ) : (
              <>
                <h2>{movie.Movie_Name}</h2> {/* Movie Name */}
                <p>{movie.Description}</p>  {/* Movie Description */}
                <p>Rating: {movie.Rating}</p> {/* Movie Rating */}
                <button onClick={() => handleEdit(movie)}>Edit</button>
                <button onClick={() => deleteMovie(movie.id)}>Delete</button>
              </>
            )}
          </div>
        ))
      ) : (
        <p>No movies to display.</p>
      )}
    </div>
  );
};

export default MovieList;
