import React, { useState } from 'react';

const MovieList = ({ movies, editMovie, deleteMovie }) => {
  const [editingId, setEditingId] = useState(null);
  const [editedMovie, setEditedMovie] = useState({ name: '', details: '', rating: '' });

  const handleEdit = (movie) => {
    setEditingId(movie.id);
    setEditedMovie(movie);
  };

  const handleSave = () => {
    editMovie(editingId, editedMovie);
    setEditingId(null);
  };

  return (
    <div>
      {movies.map((movie) => (
        <div key={movie.id}>
          {editingId === movie.id ? (
            <>
              <input
                type="text"
                value={editedMovie.name}
                onChange={(e) => setEditedMovie({ ...editedMovie, name: e.target.value })}
              />
              <input
                type="text"
                value={editedMovie.details}
                onChange={(e) => setEditedMovie({ ...editedMovie, details: e.target.value })}
              />
              <input
                type="number"
                value={editedMovie.rating}
                onChange={(e) => setEditedMovie({ ...editedMovie, rating: e.target.value })}
              />
              <button onClick={handleSave}>Save</button>
            </>
          ) : (
            <>
              <h2>{movie.name}</h2>
              <p>{movie.details}</p>
              <p>Rating: {movie.rating}</p>
              <button onClick={() => handleEdit(movie)}>Edit</button>
              <button onClick={() => deleteMovie(movie.id)}>Delete</button>
            </>
          )}
        </div>
      ))}
    </div>
  );
};

export default MovieList;
