import React, { useState, useEffect } from 'react';
import axios from 'axios';

const MovieTable = ({
  movies,
  handleEdit,
  handleDelete,
  handleSelectAll,
  handleSelectMovie,
  selectedMovies
}) => {
  return (
    <div>
      {movies.length > 0 ? (
        <table>
          <thead>
            <tr>
              <th>
                <input
                  type="checkbox"
                  onChange={handleSelectAll}
                  checked={movies.length > 0 && selectedMovies.length === movies.length}
                />
              </th>
              <th>Movie Name</th>
              <th>Description</th>
              <th>Rating</th>
              <th>Edit</th>
              <th>Delete</th>
            </tr>
          </thead>
          <tbody>
            {movies.map((movie) => (
              <tr key={movie.id}>
                <td>
                  <input
                    type="checkbox"
                    checked={selectedMovies.includes(movie.id)}
                    onChange={() => handleSelectMovie(movie.id)}
                  />
                </td>
                <td>{movie.movie_name}</td>
                <td>{movie.description}</td>
                <td>{movie.rating}</td>
                <td>
                  <button onClick={() => handleEdit(movie)}>Edit</button>
                </td>
                <td>
                  <button onClick={() => handleDelete(movie.id)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>No movies to display.</p>
      )}
    </div>
  );
};

const MovieList = () => {
  const [movies, setMovies] = useState([]);
  const [selectedMovies, setSelectedMovies] = useState([]);
  const [editingMovie, setEditingMovie] = useState(null); // To track which movie is being edited
  const [editedMovieData, setEditedMovieData] = useState({
    movie_name: '',
    description: '',
    rating: ''
  });

  // Fetch movies from backend
  useEffect(() => {
    axios
      .get('http://localhost:5001/movies')
      .then((response) => setMovies(response.data))
      .catch((error) => console.error('Error fetching movies:', error));
  }, []);

  // Handle Edit Button Click
  const handleEdit = (movie) => {
    console.log('Editing Movie:', movie); // Debugging
    setEditingMovie(movie);
    setEditedMovieData({
      movie_name: movie.movie_name,
      description: movie.description,
      rating: movie.rating,
    });
  };

  // Handle input changes for the editing form
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEditedMovieData({
      ...editedMovieData,
      [name]: value,
    });
    console.log('Edited Data:', editedMovieData); // Debugging
  };

  // Handle submit of the edited movie
  const handleEditSubmit = () => {
    if (!editingMovie) return;

    const updatedMovie = {
      ...editingMovie,
      ...editedMovieData,
    };

    console.log('Submitting Updated Movie:', updatedMovie); // Debugging

    axios
      .put(`http://localhost:5001/movies/${editingMovie.id}`, updatedMovie)
      .then((response) => {
        console.log('Update Success:', response.data); // Debugging
        setMovies(
          movies.map((movie) =>
            movie.id === editingMovie.id ? response.data : movie
          )
        );
        setEditingMovie(null); // Clear the editing state
      })
      .catch((error) => console.error('Error updating movie:', error));
  };

  return (
    <div>
      {/* Movie Table */}
      <MovieTable
        movies={movies}
        handleEdit={handleEdit}
        handleDelete={() => {}} // Placeholder, fill this out as needed
        handleSelectAll={() => {}} // Placeholder, fill this out as needed
        handleSelectMovie={() => {}} // Placeholder, fill this out as needed
        selectedMovies={[]} // Placeholder, fill this out as needed
      />

      {/* Edit Movie Form */}
      {editingMovie && (
        <div>
          <h3>Edit Movie</h3>
          <form>
            <label>
              Movie Name:
              <input
                type="text"
                name="movie_name"
                value={editedMovieData.movie_name}
                onChange={handleInputChange}
              />
            </label>
            <label>
              Description:
              <input
                type="text"
                name="description"
                value={editedMovieData.description}
                onChange={handleInputChange}
              />
            </label>
            <label>
              Rating:
              <input
                type="text"
                name="rating"
                value={editedMovieData.rating}
                onChange={handleInputChange}
              />
            </label>
            <button type="button" onClick={handleEditSubmit}>
              Save Changes
            </button>
          </form>
        </div>
      )}
    </div>
  );
};

export default MovieList;
