import React, { useState } from 'react';
import axios from 'axios';

const MovieActions = ({ movie, onSave }) => {
  const [editedMovie, setEditedMovie] = useState(movie);

  const handleSave = () => {
    if (editedMovie.Movie_Name && editedMovie.Description && !isNaN(editedMovie.Rating)) {
      axios.put(`http://localhost:5001/movies/${movie.id}`, editedMovie)
        .then(() => {
          onSave(); // Call onSave to notify the parent component that the movie has been edited
        })
        .catch((error) => {
          console.error('There was an error saving the movie:', error);
        });
    } else {
      alert('Please fill out all fields correctly.');
    }
  };

  const handleCancel = () => {
    onSave(); // Exit editing mode without saving
  };

  return (
    <div>
      <h3>Edit Movie</h3>
      <input
        type="text"
        value={editedMovie.Movie_Name}
        placeholder="Movie Name"
        onChange={(e) => setEditedMovie({ ...editedMovie, Movie_Name: e.target.value })}
      />
      <input
        type="text"
        value={editedMovie.Description}
        placeholder="Description"
        onChange={(e) => setEditedMovie({ ...editedMovie, Description: e.target.value })}
      />
      <input
        type="number"
        value={editedMovie.Rating}
        placeholder="Rating"
        onChange={(e) => setEditedMovie({ ...editedMovie, Rating: e.target.value })}
      />
      <button onClick={handleSave}>Save</button>
      <button onClick={handleCancel}>Cancel</button>
    </div>
  );
};

export default MovieActions;
