import React, { useState, useEffect } from 'react';

// Function to display movies in a table
const MovieTable = ({ movies, handleEdit, handleDelete }) => {
  return (
    <div>
      {movies.length > 0 ? (
        <table>
          <thead>
            <tr>
              <th>Movie Name</th>
              <th>Description</th>
              <th>Rating</th>
              <th>Edit</th>
              <th>Delete</th>
            </tr>
          </thead>
          <tbody>
            {movies.map((movie) => (
              <tr key={movie.id}>
                <td>{movie.Movie_Name}</td>
                <td>{movie.Description}</td>
                <td>{movie.Rating}</td>
                <td>
                  <button onClick={() => handleEdit(movie)}>Edit</button>
                </td>
                <td>
                  <button onClick={() => handleDelete(movie.id)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>No movies to display.</p>
      )}
    </div>
  );
};

const MovieList = () => {
  const [movies, setMovies] = useState([]); // State to store movie list
  const [editingId, setEditingId] = useState(null); // State to track movie being edited
  const [editedMovie, setEditedMovie] = useState({ Movie_Name: '', Description: '', Rating: '' }); // State for editing

  
  // Function to cancel editing
  const handleCancel = () => {
    setEditingId(null); // Exit editing mode without saving
    setEditedMovie({ Movie_Name: '', Description: '', Rating: '' }); // Reset form
  };

  return (
    <div>
      {/* Editing form */}
      {editingId !== null ? (
        <div>
          <input
            type="text"
            value={editedMovie.Movie_Name}
            placeholder="Movie Name"
            onChange={(e) => setEditedMovie({ ...editedMovie, Movie_Name: e.target.value })}
          />
          <input
            type="text"
            value={editedMovie.Description}
            placeholder="Description"
            onChange={(e) => setEditedMovie({ ...editedMovie, Description: e.target.value })}
          />
          <input
            type="number"
            value={editedMovie.Rating}
            placeholder="Rating"
            onChange={(e) => setEditedMovie({ ...editedMovie, Rating: e.target.value })}
          />
          <button onClick={handleSave}>Save</button>
          <button onClick={handleCancel}>Cancel</button>
        </div>
      ) : null}
    </div>
  );
};

export default MovieList;
