import React, { useState, useEffect } from 'react';
import axios from 'axios';

// Function to display movies in a table
const MovieTable = ({ movies, handleEdit, handleDelete }) => {
  return (
    <div>
      {movies.length > 0 ? (
        <table>
          <thead>
            <tr>
              <th>Movie Name</th>
              <th>Description</th>
              <th>Rating</th>
              <th>Edit</th>
              <th>Delete</th>
            </tr>
          </thead>
          <tbody>
            {movies.map((movie) => (
              <tr key={movie.id}>
                <td>{movie.Movie_Name}</td>
                <td>{movie.Description}</td>
                <td>{movie.Rating}</td>
                <td>
                  <button onClick={() => handleEdit(movie)}>Edit</button>
                </td>
                <td>
                  <button onClick={() => handleDelete(movie.id)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>No movies to display.</p>
      )}
    </div>
  );
};

const MovieList = () => {
  const [movies, setMovies] = useState([]); // State to store movie list
  const [editingId, setEditingId] = useState(null); // State to track movie being edited
  const [editedMovie, setEditedMovie] = useState({ Movie_Name: '', Description: '', Rating: '' }); // State for editing

  // Fetch movies from the backend when the component mounts
  useEffect(() => {
    axios
      .get('http://localhost:5001/movies')
      .then((response) => {
        setMovies(response.data); // Update state with fetched movies
      })
      .catch((error) => {
        console.error('There was an error fetching the movies:', error);
      });
  }, []);

  // Function to handle editing a movie
  const handleEdit = (movie) => {
    setEditingId(movie.id); // Set the current movie to edit
    setEditedMovie(movie); // Pre-fill the form with movie data
  };

  // Function to handle saving the edited movie
  const handleSave = () => {
    if (editedMovie.Movie_Name && editedMovie.Description && !isNaN(editedMovie.Rating)) {
      axios
        .put(`http://localhost:5001/movies/${editingId}`, editedMovie)
        .then(() => {
          const updatedMovies = movies.map((movie) =>
            movie.id === editingId ? { ...movie, ...editedMovie } : movie
          );
          setMovies(updatedMovies); // Update state with new movie data
          setEditingId(null); // Exit editing mode
        })
        .catch((error) => {
          console.error('There was an error saving the movie:', error);
        });
    } else {
      alert('Please fill out all fields correctly.');
    }
  };

  // Function to handle deleting a movie
  const handleDelete = (id) => {
    axios
      .delete(`http://localhost:5001/movies/${id}`)
      .then(() => {
        const updatedMovies = movies.filter((movie) => movie.id !== id);
        setMovies(updatedMovies); // Update state to remove deleted movie
      })
      .catch((error) => {
        console.error('There was an error deleting the movie:', error);
      });
  };

  // Function to cancel editing
  const handleCancel = () => {
    setEditingId(null); // Exit editing mode without saving
    setEditedMovie({ Movie_Name: '', Description: '', Rating: '' }); // Reset form
  };

  return (
    <div>
      {/* Editing form */}
      {editingId !== null ? (
        <div>
          <input
            type="text"
            value={editedMovie.Movie_Name}
            placeholder="Movie Name"
            onChange={(e) => setEditedMovie({ ...editedMovie, Movie_Name: e.target.value })}
          />
          <input
            type="text"
            value={editedMovie.Description}
            placeholder="Description"
            onChange={(e) => setEditedMovie({ ...editedMovie, Description: e.target.value })}
          />
          <input
            type="number"
            value={editedMovie.Rating}
            placeholder="Rating"
            onChange={(e) => setEditedMovie({ ...editedMovie, Rating: e.target.value })}
          />
          <button onClick={handleSave}>Save</button>
          <button onClick={handleCancel}>Cancel</button>
        </div>
      ) : null}

      {/* Movie Table */}
      <MovieTable movies={movies} handleEdit={handleEdit} handleDelete={handleDelete} />
    </div>
  );
};

export default MovieList;
