import React, { useState, useEffect } from 'react';
import axios from 'axios';
import MovieList from './components/MovieList';
import AddMovieForm from './components/AddMovieForm';



const API_URL = 'http://localhost:5001/movies';

function App() {
  const [movies, setMovies] = useState([]);


  // Fetch movies from the backend
  const fetchMovies = async () => {
      await axios.get('http://localhost:5001/movies').then
      ((res)=>{
        console.log(res.data);
        setMovies(res.data);
      });

  };
  useEffect(() => {
    fetchMovies();
  }, []);


  const addMovie = async (newMovie) => {
    try {
      await axios.post("http://localhost:5001/movies",newMovie);
      fetchMovies();
    } catch (error) {
      console.error("There was an error adding the movie:", error);
    }
  };

  const editMovie = async (id, updatedMovie) => {
    try {
      await axios.put(`${API_URL}/${id}`, updatedMovie);
      fetchMovies();
    } catch (error) {
      console.error("There was an error updating the movie:", error);
    }
  };

  const deleteMovie = async (id) => {
    try {
      await axios.delete(`${API_URL}/${id}`);
      fetchMovies();
    } catch (error) {
      console.error("There was an error deleting the movie:", error);
    }
  };

  return (
    <div className="App">
      <h1>Movie App</h1>

      <MovieList movies={movies} editMovie={editMovie} deleteMovie={deleteMovie} />
    </div>
  );
}

export default App;
